// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./tokens/BaseERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Create2.sol";

contract TokenFactory is Ownable {
    event TokenCreated(
        address indexed tokenAddress,
        string name,
        string symbol,
        string tokenType,
        address indexed owner
    );

    mapping(address => bool) public isTokenCreatedHere;

    error InvalidInitialSupply();
    error InvalidMaxSupply();

    constructor(address initialOwner) {
        _transferOwnership(initialOwner);
    }

    function createERC20(
        string memory name,
        string memory symbol,
        uint8 decimals,
        uint256 initialSupply,
        uint256 maxSupply,
        bool mintable,
        bytes32 salt
    ) external returns (address) {
        if (maxSupply > 0 && initialSupply > maxSupply) revert InvalidInitialSupply();

        bytes memory bytecode = abi.encodePacked(
            type(BaseERC20).creationCode,
            abi.encode(name, symbol, decimals, initialSupply, maxSupply, mintable, msg.sender)
        );

        address tokenAddress = Create2.deploy(0, salt, bytecode);

        isTokenCreatedHere[tokenAddress] = true;
        emit TokenCreated(tokenAddress, name, symbol, "ERC20", msg.sender);

        return tokenAddress;
    }

    function predictERC20Address(
        string memory name,
        string memory symbol,
        uint8 decimals,
        uint256 initialSupply,
        uint256 maxSupply,
        bool mintable,
        bytes32 salt
    ) external view returns (address) {
        if (maxSupply > 0 && initialSupply > maxSupply) revert InvalidInitialSupply();

        bytes memory bytecode = abi.encodePacked(
            type(BaseERC20).creationCode,
            abi.encode(name, symbol, decimals, initialSupply, maxSupply, mintable, msg.sender)
        );

        return Create2.computeAddress(salt, keccak256(bytecode));
    }

    function createToken(string memory name, string memory symbol, uint256 initialSupply) public {
        require(bytes(name).length > 0, "Name cannot be empty");
        require(bytes(symbol).length > 0, "Symbol cannot be empty");
        require(initialSupply > 0, "Supply must be positive");
        // ... reste du code
    }
}

contracts/TokenForgeFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./TokenForgeToken.sol";

contract TokenForgeFactory is Ownable, ReentrancyGuard {
    // Frais de création en USDT/USDC
    uint256 public creationFee;
    // Adresse du stablecoin accepté (USDT/USDC)
    address public stablecoinAddress;

    // Mapping des tokens créés par créateur
    mapping(address => address[]) public tokensByCreator;
    // Mapping des détails des tokens
    mapping(address => TokenDetails) public tokenDetails;

    struct TokenDetails {
        string name;
        string symbol;
        uint8 decimals;
        uint256 totalSupply;
        address owner;
        bool burnable;
        bool mintable;
        bool pausable;
        uint256 creationTime;
    }

    event TokenCreated(
        address indexed tokenAddress,
        address indexed owner,
        string name,
        string symbol,
        uint256 totalSupply
    );

    constructor(address _stablecoinAddress, uint256 _creationFee) {
        stablecoinAddress = _stablecoinAddress;
        creationFee = _creationFee;
    }

    function createToken(
        string memory name,
        string memory symbol,
        uint8 decimals,
        uint256 totalSupply,
        bool burnable,
        bool mintable,
        bool pausable
    ) external nonReentrant returns (address) {
        // Vérifier et transférer les frais
        require(IERC20(stablecoinAddress).transferFrom(
            msg.sender,
            address(this),
            creationFee
        ), "Fee transfer failed");

        // Déployer le nouveau token
        TokenForgeToken newToken = new TokenForgeToken(
            name,
            symbol,
            decimals,
            totalSupply,
            msg.sender,
            burnable,
            mintable,
            pausable
        );

        // Enregistrer les détails
        tokensByCreator[msg.sender].push(address(newToken));
        tokenDetails[address(newToken)] = TokenDetails({
            name: name,
            symbol: symbol,
            decimals: decimals,
            totalSupply: totalSupply,
            owner: msg.sender,
            burnable: burnable,
            mintable: mintable,
            pausable: pausable,
            creationTime: block.timestamp
        });

        emit TokenCreated(
            address(newToken),
            msg.sender,
            name,
            symbol,
            totalSupply
        );

        return address(newToken);
    }

    function getTokensByCreator(address creator) 
        external 
        view 
        returns (address[] memory) 
    {
        return tokensByCreator[creator];
    }

    function updateCreationFee(uint256 newFee) external onlyOwner {
        creationFee = newFee;
    }

    function withdrawFees() external onlyOwner {
        uint256 balance = IERC20(stablecoinAddress).balanceOf(address(this));
        require(IERC20(stablecoinAddress).transfer(owner(), balance), 
            "Withdrawal failed");
    }
}

contracts/TokenForgeLaunchpad.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./interfaces/ITokenForgeToken.sol";

contract TokenForgeLaunchpad is ReentrancyGuard, Ownable {
    using SafeMath for uint256;

    struct Pool {
        ITokenForgeToken token;
        uint256 tokenPrice;
        uint256 hardCap;
        uint256 softCap;
        uint256 minContribution;
        uint256 maxContribution;
        uint256 startTime;
        uint256 endTime;
        uint256 totalRaised;
        uint256 totalTokensSold;
        bool finalized;
        bool cancelled;
        mapping(address => uint256) contributions;
    }

    mapping(uint256 => Pool) public pools;
    uint256 public poolCount;
    IERC20 public platformToken; // TKN token
    uint256 public platformFee; // in basis points (1% = 100)

    event PoolCreated(uint256 indexed poolId, address indexed token);
    event Contributed(uint256 indexed poolId, address indexed contributor, uint256 amount);
    event TokensClaimed(uint256 indexed poolId, address indexed contributor, uint256 amount);
    event PoolFinalized(uint256 indexed poolId);
    event PoolCancelled(uint256 indexed poolId);
    event RefundClaimed(uint256 indexed poolId, address indexed contributor, uint256 amount);

    constructor(address _platformToken) {
        platformToken = IERC20(_platformToken);
        platformFee = 100; // 1%
    }

    function createPool(
        address _token,
        uint256 _tokenPrice,
        uint256 _hardCap,
        uint256 _softCap,
        uint256 _minContribution,
        uint256 _maxContribution,
        uint256 _startTime,
        uint256 _endTime
    ) external nonReentrant returns (uint256) {
        require(_token != address(0), "Invalid token");
        require(_startTime > block.timestamp, "Start time must be in future");
        require(_endTime > _startTime, "End time must be after start time");
        require(_hardCap > _softCap, "Hard cap must be greater than soft cap");
        require(_maxContribution >= _minContribution, "Max contribution must be >= min");

        uint256 poolId = poolCount++;
        Pool storage pool = pools[poolId];

        pool.token = ITokenForgeToken(_token);
        pool.tokenPrice = _tokenPrice;
        pool.hardCap = _hardCap;
        pool.softCap = _softCap;
        pool.minContribution = _minContribution;
        pool.maxContribution = _maxContribution;
        pool.startTime = _startTime;
        pool.endTime = _endTime;

        // Transfer tokens to contract
        uint256 tokensRequired = _hardCap.mul(1e18).div(_tokenPrice);
        require(pool.token.transferFrom(msg.sender, address(this), tokensRequired), "Token transfer failed");

        emit PoolCreated(poolId, _token);
        return poolId;
    }

    function contribute(uint256 _poolId) external payable nonReentrant {
        Pool storage pool = pools[_poolId];
        require(block.timestamp >= pool.startTime, "Pool not started");
        require(block.timestamp <= pool.endTime, "Pool ended");
        require(!pool.finalized && !pool.cancelled, "Pool not active");
        require(msg.value >= pool.minContribution, "Below min contribution");
        require(msg.value <= pool.maxContribution, "Above max contribution");
        require(pool.totalRaised.add(msg.value) <= pool.hardCap, "Hard cap reached");

        uint256 userTotal = pool.contributions[msg.sender].add(msg.value);
        require(userTotal <= pool.maxContribution, "Would exceed max contribution");

        pool.contributions[msg.sender] = userTotal;
        pool.totalRaised = pool.totalRaised.add(msg.value);

        emit Contributed(_poolId, msg.sender, msg.value);
    }

    function finalizePool(uint256 _poolId) external nonReentrant {
        Pool storage pool = pools[_poolId];
        require(msg.sender == owner() || msg.sender == pool.token.owner(), "Not authorized");
        require(block.timestamp > pool.endTime || pool.totalRaised >= pool.hardCap, "Cannot finalize yet");
        require(!pool.finalized && !pool.cancelled, "Pool not active");
        require(pool.totalRaised >= pool.softCap, "Soft cap not reached");

        pool.finalized = true;

        // Calculate and transfer platform fee
        uint256 feeAmount = pool.totalRaised.mul(platformFee).div(10000);
        payable(owner()).transfer(feeAmount);

        // Transfer remaining funds to token owner
        payable(pool.token.owner()).transfer(pool.totalRaised.sub(feeAmount));

        emit PoolFinalized(_poolId);
    }

    function cancelPool(uint256 _poolId) external {
        Pool storage pool = pools[_poolId];
        require(msg.sender == owner() || msg.sender == pool.token.owner(), "Not authorized");
        require(!pool.finalized && !pool.cancelled, "Pool not active");

        pool.cancelled = true;

        // Return tokens to owner
        uint256 remainingTokens = pool.token.balanceOf(address(this));
        require(pool.token.transfer(pool.token.owner(), remainingTokens), "Token return failed");

        emit PoolCancelled(_poolId);
    }

    function claimTokens(uint256 _poolId) external nonReentrant {
        Pool storage pool = pools[_poolId];
        require(pool.finalized, "Pool not finalized");

        uint256 contribution = pool.contributions[msg.sender];
        require(contribution > 0, "No contribution");

        uint256 tokenAmount = contribution.mul(1e18).div(pool.tokenPrice);
        pool.contributions[msg.sender] = 0;
        pool.totalTokensSold = pool.totalTokensSold.add(tokenAmount);

        require(pool.token.transfer(msg.sender, tokenAmount), "Token transfer failed");

        emit TokensClaimed(_poolId, msg.sender, tokenAmount);
    }

    function claimRefund(uint256 _poolId) external nonReentrant {
        Pool storage pool = pools[_poolId];
        require(pool.cancelled || 
                (block.timestamp > pool.endTime && pool.totalRaised < pool.softCap),
                "Refund not available");

        uint256 contribution = pool.contributions[msg.sender];
        require(contribution > 0, "No contribution");

        pool.contributions[msg.sender] = 0;
        payable(msg.sender).transfer(contribution);

        emit RefundClaimed(_poolId, msg.sender, contribution);
    }

    function getPoolInfo(uint256 _poolId) external view returns (
        address token,
        uint256 tokenPrice,
        uint256 hardCap,
        uint256 softCap,
        uint256 totalRaised,
        uint256 startTime,
        uint256 endTime,
        bool finalized,
        bool cancelled
    ) {
        Pool storage pool = pools[_poolId];
        return (
            address(pool.token),
            pool.tokenPrice,
            pool.hardCap,
            pool.softCap,
            pool.totalRaised,
            pool.startTime,
            pool.endTime,
            pool.finalized,
            pool.cancelled
        );
    }

    function getContribution(uint256 _poolId, address _contributor) external view returns (uint256) {
        return pools[_poolId].contributions[_contributor];
    }

    function setPlatformFee(uint256 _newFee) external onlyOwner {
        require(_newFee <= 1000, "Fee too high"); // Max 10%
        platformFee = _newFee;
    }
}

contracts/TokenForgePlans.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract TokenForgePlans is Ownable, ReentrancyGuard {
    enum PlanType { Apprenti, Forgeron, MaitreForgeron }

    struct Plan {
        string name;
        uint256 price;
        bool isActive;
    }

    mapping(PlanType => Plan) public plans;
    mapping(address => PlanType) public userPlans;

    event PlanPurchased(address indexed user, PlanType planType, uint256 price);
    event PlanPriceUpdated(PlanType planType, uint256 newPrice);

    constructor() {
        // Initialisation des plans
        plans[PlanType.Apprenti] = Plan("Apprenti Forgeron", 0, true);
        plans[PlanType.Forgeron] = Plan("Forgeron", 0.3 ether, true);
        plans[PlanType.MaitreForgeron] = Plan("Maitre Forgeron", 1 ether, true);
    }

    function purchasePlan(PlanType planType) external payable nonReentrant {
        require(plans[planType].isActive, "Plan not available");
        require(msg.value >= plans[planType].price, "Insufficient payment");

        userPlans[msg.sender] = planType;

        emit PlanPurchased(msg.sender, planType, msg.value);

        // Remboursement du surplus si nécessaire
        if (msg.value > plans[planType].price) {
            payable(msg.sender).transfer(msg.value - plans[planType].price);
        }
    }

    function updatePlanPrice(PlanType planType, uint256 newPrice) external onlyOwner {
        plans[planType].price = newPrice;
        emit PlanPriceUpdated(planType, newPrice);
    }

    function getUserPlan(address user) external view returns (PlanType) {
        return userPlans[user];
    }

    function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
}

contracts/TokenForgeStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract TokenForgeStaking is ReentrancyGuard, Ownable {
    IERC20 public stakingToken;

    struct StakeInfo {
        uint256 amount;
        uint256 since;
        uint256 claimedRewards;
    }

    struct Pool {
        uint256 rewardRate; // Rewards per second per token
        uint256 totalStaked;
        uint256 lastUpdateTime;
        mapping(address => StakeInfo) stakes;
    }

    Pool public stakingPool;
    uint256 public constant MINIMUM_STAKE_TIME = 1 days;

    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 amount);

    constructor(address _stakingToken) {
        stakingToken = IERC20(_stakingToken);
        stakingPool.rewardRate = 1e15; // 0.001 tokens per second per staked token
        stakingPool.lastUpdateTime = block.timestamp;
    }

    function stake(uint256 _amount) external nonReentrant {
        require(_amount > 0, "Cannot stake 0");
        updatePool();

        stakingPool.totalStaked += _amount;
        stakingPool.stakes[msg.sender].amount += _amount;
        if (stakingPool.stakes[msg.sender].since == 0) {
            stakingPool.stakes[msg.sender].since = block.timestamp;
        }

        require(stakingToken.transferFrom(msg.sender, address(this), _amount), "Stake failed");
        emit Staked(msg.sender, _amount);
    }

    function withdraw(uint256 _amount) external nonReentrant {
        StakeInfo storage userStake = stakingPool.stakes[msg.sender];
        require(_amount > 0 && _amount <= userStake.amount, "Invalid amount");
        require(block.timestamp >= userStake.since + MINIMUM_STAKE_TIME, "Staking period not met");

        updatePool();
        claimRewards();

        stakingPool.totalStaked -= _amount;
        userStake.amount -= _amount;
        if (userStake.amount == 0) {
            userStake.since = 0;
        }

        require(stakingToken.transfer(msg.sender, _amount), "Withdraw failed");
        emit Withdrawn(msg.sender, _amount);
    }

    function claimRewards() public nonReentrant {
        updatePool();
        uint256 rewards = calculateRewards(msg.sender);
        if (rewards > 0) {
            stakingPool.stakes[msg.sender].claimedRewards += rewards;
            require(stakingToken.transfer(msg.sender, rewards), "Reward transfer failed");
            emit RewardsClaimed(msg.sender, rewards);
        }
    }

    function calculateRewards(address _user) public view returns (uint256) {
        StakeInfo storage userStake = stakingPool.stakes[_user];
        if (userStake.amount == 0) {
            return 0;
        }

        uint256 timeStaked = block.timestamp - userStake.since;
        uint256 rewards = (userStake.amount * stakingPool.rewardRate * timeStaked) / 1e18;
        return rewards - userStake.claimedRewards;
    }

    function updatePool() internal {
        stakingPool.lastUpdateTime = block.timestamp;
    }

    function setRewardRate(uint256 _newRate) external onlyOwner {
        updatePool();
        stakingPool.rewardRate = _newRate;
    }

    function getUserStake(address _user) external view returns (uint256 amount, uint256 since, uint256 claimedRewards) {
        StakeInfo storage userStake = stakingPool.stakes[_user];
        return (userStake.amount, userStake.since, userStake.claimedRewards);
    }

    function getPoolInfo() external view returns (uint256 totalStaked, uint256 rewardRate, uint256 lastUpdateTime) {
        return (stakingPool.totalStaked, stakingPool.rewardRate, stakingPool.lastUpdateTime);
    }
}

contracts/TokenForgeToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract TokenForgeToken is ERC20, ERC20Burnable, Pausable, Ownable {
    uint8 private _decimals;
    bool public isMintable;
    bool public isBurnable;

    event TokenMinted(address indexed to, uint256 amount);
    event TokenBurned(address indexed from, uint256 amount);

    constructor(
        string memory name,
        string memory symbol,
        uint8 tokenDecimals,
        uint256 initialSupply,
        address tokenOwner,
        bool burnable,
        bool mintable,
        bool pausable
    ) ERC20(name, symbol) {
        _decimals = tokenDecimals;
        isMintable = mintable;
        isBurnable = burnable;

        // Transférer la propriété au créateur
        _transferOwnership(tokenOwner);

        // Mint initial supply
        _mint(tokenOwner, initialSupply * (10 ** decimals()));

        // Si pausable est true, mettre le token en pause initialement
        if (pausable) {
            _pause();
        }
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function mint(address to, uint256 amount) public onlyOwner {
        require(isMintable, "Minting is disabled");
        _mint(to, amount);
        emit TokenMinted(to, amount);
    }

    function burn(uint256 amount) public override {
        require(isBurnable, "Burning is disabled");
        super.burn(amount);
        emit TokenBurned(_msgSender(), amount);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
    }
}

